mapper.update:
UPDATE article		

updateDate = NOW(),
title = #{title},
`body`= #{body}

WHERE id = #{id}

UPDATE article		

hitCount = hitCount +1

WHERE id = #{id}

UPDATE article		

goodReactionPoint = goodReactionPoint +1

WHERE id = #{relId}

UPDATE article		

badReactionPoint = badReactionPoint +1

WHERE id = #{relId}

UPDATE article		

goodReactionPoint = goodReactionPoint -1

WHERE id = #{relId}

UPDATE article		

badReactionPoint = badReactionPoint -1

WHERE id = #{relId}
